mapper.select:
SELECT t.*,jbo.name as pName FROM JY_BASE_ORG t LEFT JOIN JY_BASE_ORG jbo ON jbo.id=t.pId WHERE 1=1
AND t.id=#{id}
AND t.pId=#{pId}
ORDER BY t.createTime

mapper.delete:
DELETE from JY_BASE_ORG 
WHERE id in(

		#{o.id}

	)

mapper.update:
UPDATE JY_BASE_ORG 
SET name=#{name},
	description=#{description},
	updateTime =#{updateTime}
WHERE id=#{id}

mapper.select:
SELECT t.id as id, t.name as name, t.pId as pId FROM JY_BASE_ORG t WHERE 1=1
and t.cid=#{company}
ORDER BY t.id,t.name

SELECT t.id as id, t.name as name, t.pId as pId FROM JY_BASE_ORG t where 1=1
and t.cid=#{company}
ORDER BY t.id,t.name

